import { TOGGLE_OPTION } from './action_type';

const initialState = {
  list: [
    {
      id: 1,
      name: 'canceled',
      check: false
    },
    {
      id: 2,
      name: 'draft',
      check: true
    },
    {
      id: 3,
      name: 'with pickup',
      check: true
    },
    {
      id: 4,
      name: 'printed',
      check: true
    }
  ]
};


function storeReducer(state = initialState, { type, payload }) {
  if (type === TOGGLE_OPTION) {
    return {
      payload: !payload
    }
  } else {
    return state
  }
}

export default storeReducer;
