const initialState = [
  {
    id: 1,
    name: 'canceled',
    check: false
  },
  {
    id: 2,
    name: 'draft',
    check: true
  },
  {
    id: 3,
    name: 'with pickup',
    check: true
  },
  {
    id: 4,
    name: 'printed',
    check: true
  }
]

function storeReducer(state = initialState, action) {
  if (action.type === 'TOGGLE_TODO') {
    const { id } = action.payload;
    return {
      ...state,
      check: !state.byIds[id].check
    }
  } else {
    return state
  }
}

export default storeReducer;
