import './App.css';
import { useState } from 'react';
import { toggleOption } from './actions';
import { useDispatch } from 'react-redux';

function App({ data }) {
  const [isActive, setActive] = useState();
  const handleToggle = () => {
    setActive(!isActive);
  }
  const dispacth = useDispatch();
  const defaultOption = data.filter(item => item.check === true);

  const handleToggleOption = (e) => {
    const action = toggleOption(!e.check);
    dispacth(action);
  }
  return (
    <div className="App">
      <div className="search-option">
        <p><strong>Search option</strong></p>
        <div onClick={handleToggle} className="show-option">
          {
            defaultOption.map((item, index) => {
              return (
                <span key={index}>{item.name}</span>
              )
            })
          }
        </div>
        <div className={isActive ? 'option-box show' : 'option-box'}>
          {
            data.map((item, index) => {
              return (
                <label className="item" key={index}>
                  <input onChange={() => handleToggleOption(item)} type="checkbox" />
                  <span>{item.name}</span>
                </label>
              )
            })
          }
        </div>
      </div>
      <div className="result">
        <p><strong>Result</strong></p>
        <div className="show-result">
        </div>
      </div>
    </div>
  );
}

export default App;
