import './App.css';
import { useState } from 'react';
import { toggleOption } from './actions';
import { useDispatch, useSelector } from 'react-redux';

function App({ data }) {
  const [isActive, setActive] = useState();
  const handleToggle = () => {
    setActive(!isActive);
  }
  const dispacth = useDispatch();

  const handleToggleOption = (e) => {
    const action = toggleOption(e);
    dispacth(action);
  }
  const list_option = useSelector(state => state);
  const default_option = list_option.map(item => item.name)
