import { TOGGLE_OPTION } from './action_type';

const initialState = [
  {
    id: 1,
    name: 'canceled',
    check: false
  },
  {
    id: 2,
    name: 'draft',
    check: true
  },
  {
    id: 3,
    name: 'with pickup',
    check: true
  },
  {
    id: 4,
    name: 'printed',
    check: true
  }
];


function storeReducer(state = initialState, { type, payload }) {
  switch (type) {
    case TOGGLE_OPTION: {
      const array = [...state];
      array.map(item => {
        if (item.id === payload.id) {
          item.check = !payload.check;
        }
      });
      state = [...array];
      return state;
    }
  }
}

export default storeReducer;
