import { TOGGLE_OPTION } from './action';

const initialState = [
  {
    id: 1,
    name: 'canceled',
    check: false
  },
  {
    id: 2,
    name: 'draft',
    check: true
  },
  {
    id: 3,
    name: 'with pickup',
    check: true
  },
  {
    id: 4,
    name: 'printed',
    check: true
  }
]

function storeReducer(state = initialState, action) {
  if (action.type === TOGGLE_OPTION) {
    const { id } = action.payload;
    return {
      ...state,
      byId: {
        ...state.byIds,
        [id]: {
          ...state.byIds[id],
          check: !state.byIds[id].check
        }
      }
    }
  } else {
    return state
  }
}

export default storeReducer;
