import './App.css';
import { useState } from 'react';
import { createStore } from 'redux';
import { Provider } from 'react-redux';
import store_reducer from './reducer';

const store = createStore(store_reducer);

function App() {
  const [isActive, setActive] = useState();
  const handleToggle = () => {
    setActive(!isActive);
  }

  return (
    <Provider>
      <div className="App">
        <div className="search-option">
          <p><strong>Search option</strong></p>
          <div onClick={handleToggle} className="show-option">
            {/* {
              option.map(item => {
                return (
                  <span>{item.name}</span>
                )
              })
            } */}
          </div>
          <div className={isActive ? 'option-box show' : 'option-box'}>
            {/* {
              option.map((item, index) => {
                return (
                  <label className="item" key={index}>
                    <input type="checkbox" />
                    <span>{item.name}</span>
                  </label>
                )
              })
            } */}
          </div>
        </div>
        <div className="result">
          <p><strong>Result</strong></p>
          <div className="show-result">
          </div>
        </div>
      </div>
    </Provider>
  );
}

export default App;
